import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddNewUser.module.css";
import ErrorModal from './components/UI/ErrorModal';

const NewUser = (props) => {
  const [username, enteredUsername] = useState("");
  const [age, enteredAge] = useState("");

  const addUser = (event) => {
    event.preventDefault();
    if(username.trim().length === 0 || age.trim().length === 0){
        return;
    }
    if(+age < 1){
        return;
    }

    props.onAdd(username, age);
    enteredAge("");
    enteredUsername("");
  };

  const usernameChange = (event) => {
    enteredUsername(event.target.value);
  };
  const ageChange = (event) => {
    enteredAge(event.target.value);
  };

  return (
    <div>
    <ErrorModal title='An error has occured!' message='Your input is invalid!'></ErrorModal>
    <Card className={classes.input}>
      <form onSubmit={addUser}>
        <label htmlFor="uername">Username</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={usernameChange}
          placeholder="What is your username?"
        ></input>
        <label htmlFor="age">Age</label>
        <input
          id="age"
          type="number"
          value={age}
          onChange={ageChange}
          placeholder="How old are you?"
        ></input>
        <Button type="submit">Add new user</Button>
      </form>
    </Card>
    </div>
  );
};

export default NewUser;
