import React, {useState} from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from './AddNewUser.module.css'

const NewUser = props => {
    const [username, enteredUsername] = useState('');
    const [age, enteredAge] = useState('');

    const addUser = event => {
        event.preventDefault();
        console.log(username, age);
    };

    const usernameChange = event => {
        enteredUsername(event.target.value)
    };
    const ageChange = event => {
        enteredAge(event.target.value)
    };



    return (
        <Card className={classes.input}>
        <form onSubmit={addUser}>
            <label htmlFor="uername">Username</label>
            <input id="username" type='text' onChange={usernameChange} placeholder="What is your username?"></input>
            <label htmlFor="age">Age</label>
            <input id="age" type='number' onChange={ageChange} placeholder="How old are you?"></input>
            <Button type="submit">Add new user</Button>
        </form>
        </Card>
    );
};

export default NewUser;
